// image assets are stored as the <img> element
const img = Hyperdeck.Get('assets1').get('css/arrow-25x19.png');

// json assets are parsed to a javascript object
const json = Hyperdeck.Get('assets1').get('workbooks/fonts.json');

const pre = document.createElement('pre');
pre.innerText = JSON.stringify(json);

const div = document.createElement('div');
div.appendChild(img);
div.appendChild(pre);
return div;
