template = Handlebars.compile(Hyperdeck.Get('template_string'));

rows = Hyperdeck.Get('data1');
$('#output').append('<ul />');
for (i=0,l=rows.length;i<l;i++) {
	const row = rows[i];
	$('#output > ul').append(template(row));
}

window.show_women = function() {
	$('#output li').each(function(el) {
		if ($(this).attr('data-gender') == "M") {
			$(this).toggle();
		}
	});
}

$("#output").prepend('<button onclick="show_women();">Filter: Women only</button>');

 Tabletop.init({ 
	 key: Hyperdeck.Get('spreadsheetKey'),
   callback: function(data, tabletop) {
		 Hyperdeck.Set('data1', data); },
     simpleSheet: true
   } 
)
