var hexes = Hyperdeck.Get('hexes');

var ls = [];
ls.push('<svg width="500" height="500">');

var r = 20;
var w = r / 2 * Math.sqrt(3);
var h = r / 2;

for (var i = 0; i < hexes.length; i++)
{
	var hex = hexes[i];
	var cx = 50 + w * 2 * hex.col + w * (hex.row % 2);
	var cy = 50 + h * 3 * hex.row;
	var x0 = cx + r * Math.cos(0.5 / 6 * Math.PI * 2);
	var y0 = cy + r * Math.sin(0.5 / 6 * Math.PI * 2);
	var x1 = cx + r * Math.cos(1.5 / 6 * Math.PI * 2);
	var y1 = cy + r * Math.sin(1.5 / 6 * Math.PI * 2);
	var x2 = cx + r * Math.cos(2.5 / 6 * Math.PI * 2);
	var y2 = cy + r * Math.sin(2.5 / 6 * Math.PI * 2);
	var x3 = cx + r * Math.cos(3.5 / 6 * Math.PI * 2);
	var y3 = cy + r * Math.sin(3.5 / 6 * Math.PI * 2);
	var x4 = cx + r * Math.cos(4.5 / 6 * Math.PI * 2);
	var y4 = cy + r * Math.sin(4.5 / 6 * Math.PI * 2);
	var x5 = cx + r * Math.cos(5.5 / 6 * Math.PI * 2);
	var y5 = cy + r * Math.sin(5.5 / 6 * Math.PI * 2);
	ls.push(sprintf('<polygon id="%d" stroke="black" fill="%s" points="%f,%f %f,%f %f,%f %f,%f %f,%f %f,%f" />', i, hex.fill, x0, y0, x1, y1, x2, y2, x3, y3, x4, y4, x5, y5));
}

ls.push('</svg>');

$('#draw').html(ls.join(''));

$('#draw polygon').on('mousedown', function() {
	var id = $(this).attr('id');
	hexes[id].fill = 'red';
	$(this).attr('fill', 'red');
});

var hexes = [];

for (var i = 0; i < 10; i++)
{
	for (var j = 0; j < 10; j++)
	{
		hexes.push({row:i,col:j,fill:'white'});
	}
}

Hyperdeck.Set('hexes', hexes);
