components.text:
if (first)
{
	var theassets = Hyperdeck.Get('assets1');
	AddTiles(theassets.get('http://localhost:714/mid-atlantic.txt'));
	AddGotten(theassets.get('http://localhost:714/gotten.txt'));
	first = false;
}

ctx.canvas.width = params.width;
ctx.canvas.height = params.height;

ctx.fillStyle = 'white';
ctx.fillRect(0, 0, ctx.canvas.width, ctx.canvas.height);

assets.forEach(function(file, key) {

	if (key.endsWith('.topojson') || key.endsWith('.arcpack'))
	{
		const [coordPack, pixelPack, topo] = file;
		Mapper.ProjectPackToPack(projection, coordPack, pixelPack);

		if (key == 'http://localhost:714/states.topojson')
		{
			ctx.lineWidth = 3;
		}
		else if (key == 'http://localhost:714/lowres.topojson')
		{
			ctx.lineWidth = 1;
		}

		ctx.strokeStyle = 'black';

		ctx.lineJoin = 'center';
		ctx.beginPath();
		Mapper.DrawMeshPack(ctx, pixelPack);
		ctx.stroke();
	}
});

DrawTiles(ctx, projection);
